mapper.select:
select sum(payment_amount) * 0.095 as payment_amount
from PAYMENT_TB PT
         join SERVICE_TB ST on PT.service_id = ST.service_id
where school_id = #{school_id}
  and PT.payment_type = 3;

select IF(payment_amount >= 0 ,payment_amount, payment_amount * -1 ) as payment_amount,
       service_name
from PAYMENT_TB PT
         join SERVICE_TB ST on PT.service_id = ST.service_id
where school_id = #{school_id}
  and DATE_FORMAT(DATE, '%Y-%m') = #{date_info}

select sum(payment_amount) * 0.095 as payment_amount
from PAYMENT_TB PT
         join SERVICE_TB ST on PT.service_id = ST.service_id
where school_id = #{school_id}
  and PT.payment_type = 3
  and DATE_FORMAT(DATE, '%Y-%m') = #{date_info}
